import{_ as a,c as t,o as r,m as e,a as o}from"./chunks/framework.D5hbrQnr.js";const b=JSON.parse('{"title":"第二十章　占有","description":"","frontmatter":{},"headers":[],"relativePath":"repo/law-library/001 民法典/02 第二编：物权/05 第五分编：占有/20 第二十章：占有.md","filePath":"repo/law-library/001 民法典/02 第二编：物权/05 第五分编：占有/20 第二十章：占有.md"}'),s={name:"repo/law-library/001 民法典/02 第二编：物权/05 第五分编：占有/20 第二十章：占有.md"},_=e("h1",{id:"第二十章-占有",tabindex:"-1"},[o("第二十章　占有 "),e("a",{class:"header-anchor",href:"#第二十章-占有","aria-label":'Permalink to "第二十章　占有"'},"​")],-1),n=e("p",null,"第四百五十八条　基于合同关系等产生的占有，有关不动产或者动产的使用、收益、违约责任等，按照合同约定；合同没有约定或者约定不明确的，依照有关法律规定。 第四百五十九条　占有人因使用占有的不动产或者动产，致使该不动产或者动产受到损害的，恶意占有人应当承担赔偿责任。 第四百六十条　不动产或者动产被占有人占有的，权利人可以请求返还原物及其孳息；但是，应当支付善意占有人因维护该不动产或者动产支出的必要费用。 第四百六十一条　占有的不动产或者动产毁损、灭失，该不动产或者动产的权利人请求赔偿的，占有人应当将因毁损、灭失取得的保险金、赔偿金或者补偿金等返还给权利人；权利人的损害未得到足够弥补的，恶意占有人还应当赔偿损失。 第四百六十二条　占有的不动产或者动产被侵占的，占有人有权请求返还原物；对妨害占有的行为，占有人有权请求排除妨害或者消除危险；因侵占或者妨害造成损害的，占有人有权依法请求损害赔偿。 占有人返还原物的请求权，自侵占发生之日起一年内未行使的，该请求权消灭。",-1),c=[_,n];function l(i,d,p,h,m,f){return r(),t("div",null,c)}const x=a(s,[["render",l]]);export{b as __pageData,x as default};
